import { createUseStyles } from "react-jss";
import colors from '../../Styles/Color'
import { CARD_SHADOW } from '../../Styles/Button';


const useStyles = createUseStyles({
    root: {
      padding: '40px 20px',
      marginBottom: '0px',
      marginTop: '0px',
      '&.card-shadow': CARD_SHADOW,
      '& .title': {
        color: 'salmon',
      },
      '& .subtitle': {
        color: colors['text-gray-700'],
        fontWeight: '400',
      },
      '& .button-primary': {
        background: ' #38d9a9',
        border: 0,
        borderRadius: '10px',
        fontWeight: 600,
        color: '#fff !important',
        padding: '8px 20px !important',
        '&:not([disabled]):hover': {
          background: '#0ca678',
        },
      },
      '& .BUTTON_PRIMARY_OUTLINE' :BUTTON_PRIMARY_OUTLINE,
      '& .BUTTON_LIGHT' : BUTTON_LIGHT ,
      '& .BUTTON_GRAY' :BUTTON_GRAY,
      '& .BUTTON_TRANSPARENT' : BUTTON_TRANSPARENT ,
      '& .LINK_PRIMARY' : LINK_PRIMARY ,
      '& .CARD_SHADOW' : CARD_SHADOW,
      '& .INPUT_TEXT' : INPUT_TEXT ,
      '& .SIDEBAR_TITLE' : SIDEBAR_TITLE ,
      '& .POSTITEM_CARD' :POSTITEM_CARD ,
      '& .form-link': {
        '& a': {
          color: colors['text-gray-700'],
          '& span': {
            fontWeight: 500,
          },
        },
      },
    },
  });

  export default useStyles ;
