import React, { useEffect, useState } from 'react';
import { connect } from 'react-redux';
import Helmet from 'react-helmet';
import { Container, Form } from 'react-bootstrap';
import useStyles from './Request-jss';

// Actions
import { request, clearErrors } from '../../../redux/actions/requestActions';
import { setAlert } from '../../../redux/actions/alertActions';

// App layout components
import Spinner from '../../layout/Spinner/Spinner';
import Input from './InputElement'
// Utils
import { WEBSITE_NAME } from '../../../utils/Data';


const Request = (props) => {
  const {
    error,
    loading,
    request,
    clearErrors,
    setAlert,
  } = props;

  const [req, setReq] = useState({
    orderForm: {
        name: {
            elementType: 'input',
            elementConfig: {
                type: 'text',
                placeholder: 'Full Name'
            },
            value: '',
            validation: {
                required: true
            },
            valid: false,
            touched: false
        },
        email: {
            elementType: 'input',
            elementConfig: {
                type: 'email',
                placeholder: 'Your E-Mail'
            },
            value: '',
            validation: {
                required: true,
                isEmail: true
            },
            valid: false,
            touched: false
        },
        category: {
            elementType: 'select',
            elementConfig: {
                options: [
                    {value: 'All categories', displayValue: 'All categories'},
                    {value: 'Social aid', displayValue: 'Social aid'},
                    {value: 'Health services', displayValue: 'Health services'},
                    {value: 'Solidarity', displayValue: 'Solidarity'},
                    {value: 'Other services', displayValue: 'Other services'}
                ]
            },
            value: '',
            validation: {},
            valid: true
        },
        offers: {
            elementType: 'select',
            elementConfig: {
                options: [
                    {value: 'All offers', displayValue: 'All offers'},
                ]
            },
            value: '',
            validation: {},
            valid: true
        }
    },
    formIsValid: false,
    loading: false
});

  const { name, email, category, offer } = req;



  const orderHandler = ( event ) => {
    event.preventDefault();
    setReq( { loading: true } );
    const formData = {};
    for (let formElementIdentifier in req.orderForm) {
        formData[formElementIdentifier] = req.orderForm[formElementIdentifier].value;
    }  
}

const checkValidity = (value, rules) => {
    let isValid = true;
    if (!rules) {
        return true;
    }
    
    if (rules.required) {
        isValid = value.trim() !== '' && isValid;
    }

    if (rules.minLength) {
        isValid = value.length >= rules.minLength && isValid
    }

    if (rules.maxLength) {
        isValid = value.length <= rules.maxLength && isValid
    }

    if (rules.isEmail) {
        const pattern = /[a-z0-9!#$%&'*+/=?^_`{|}~-]+(?:\.[a-z0-9!#$%&'*+/=?^_`{|}~-]+)*@(?:[a-z0-9](?:[a-z0-9-]*[a-z0-9])?\.)+[a-z0-9](?:[a-z0-9-]*[a-z0-9])?/;
        isValid = pattern.test(value) && isValid
    }

    if (rules.isNumeric) {
        const pattern = /^\d+$/;
        isValid = pattern.test(value) && isValid
    }

    return isValid;
}

const inputChangedHandler = (event, inputIdentifier) => {
    const updatedOrderForm = {
        ...req.orderForm
    };
    const updatedFormElement = { 
        ...updatedOrderForm[inputIdentifier]
    };
    updatedFormElement.value = event.target.value;
    updatedFormElement.valid = checkValidity(updatedFormElement.value, updatedFormElement.validation);
    updatedFormElement.touched = true;
    updatedOrderForm[inputIdentifier] = updatedFormElement;
    if (updatedOrderForm.category.value === 'All categories') {
        updatedOrderForm.offers.elementConfig.options = [

        ]
    }
    if (updatedOrderForm.category.value === 'Social aid') {
        updatedOrderForm.offers.elementConfig.options = [
            {value: 'Retirement', displayValue: 'Retirement'},
            {value: 'Death', displayValue: 'Death'},
            {value: 'Marriage', displayValue: 'Marriage'},
            {value: 'New born', displayValue: 'New born'},
            {value: 'Circumcision', displayValue: 'Circumcision'}
        ]
    };
    if (updatedOrderForm.category.value === 'Health services') {
        updatedOrderForm.offers.elementConfig.options = [
            {value: 'Surgery operations', displayValue: 'Surgery operations'},
            {value: 'Hammamat( mineral bath )', displayValue: 'Hammamat( mineral bath )'}
        ]
    };
    if (updatedOrderForm.category.value === 'Solidarity') {
        updatedOrderForm.offers.elementConfig.options = [
            {value: 'Aids for accident and catastrophe', displayValue: 'Aids for accident and catastrophe'},
            {value: ' Aids for the exceptional cases', displayValue: ' Aids for the exceptional cases'},
        ]
    };
    if (updatedOrderForm.category.value === 'Other services') {
        updatedOrderForm.offers.elementConfig.options =[
            {value: 'Exceptional loan', displayValue: 'Exceptional loan'},
            {value: 'Sport and cultural activities', displayValue: 'Sport and cultural activities'},
        ]
    };
    if (updatedOrderForm.category.value === 'All categories') {
        updatedOrderForm.offers.elementConfig.options =[
            {value: 'All types', displayValue: 'All types'}
        ]
    }
    
    let formIsValid = true;
    for (let inputIdentifier in updatedOrderForm) {
        formIsValid = updatedOrderForm[inputIdentifier].valid && formIsValid;
    }
    
    setReq({orderForm: updatedOrderForm, formIsValid: formIsValid});
}




const formElementsArray = [];
        for (let key in req.orderForm) {
            formElementsArray.push({
                id: key,
                config: req.orderForm[key]
            });
        }
        let form = (
            <form onSubmit={orderHandler}>
                {formElementsArray.map(formElement => (
                    <Input 
                        key={formElement.id}
                        elementType={formElement.config.elementType}
                        elementConfig={formElement.config.elementConfig}
                        value={formElement.config.value}
                        invalid={!formElement.config.valid}
                        shouldValidate={formElement.config.validation}
                        touched={formElement.config.touched}
                        changed={(event) => inputChangedHandler(event, formElement.id)} />
                ))}
            </form>
        );
       


  const onChange = (e) => setReq({ ...req, [e.target.name]: e.target.value });

  useEffect(() => {
    if (error && error.length) {
      if (typeof error === 'object') {
        error.forEach((err) => {
          setAlert(err.msg, 'danger');
        });
      } else {
        setAlert(error, 'danger');
      }

      clearErrors();
    }

    // eslint-disable-next-line
  }, [error]);

  const onSubmit = async (e) => {
    e.preventDefault();

    if (category === '' || offer === '' || email === '' || name === '' ) {
      setAlert('Please enter all fields', 'danger');
    } else {
      await request({ name , email , category , offer });
    }
  };

  const classes = useStyles();

  return (
    <>
      <Helmet>
        <title>{`${WEBSITE_NAME} | Request`}</title>
      </Helmet>
      <Container className={`${classes.page} `}>
        <div className='container-inner px-3 mt-4'>
          <div className='auth mx-auto'>
            <h4 className='mb-3 text-center '>
              <strong className='title' >Add a request</strong>
            </h4>
        
               
               

            <div >
                {form}
            </div>



           <button onClick={onsubmit} className='button-primary' >
             Send
           </button>
          </div>
        </div>
      </Container>
    </>
  );
};

const mapSateToProps = (state) => ({
  error: state.auth.error,
  loading: state.auth.loading,
});

export default connect(mapSateToProps, { request, clearErrors, setAlert })(
  Request
);
