import { createUseStyles } from 'react-jss';

const Styles = createUseStyles({
  page: {
    padding: '10px 150px 10px 150px',
    marginBottom: '10px',
    '& h1.title': {
      color: '#2d3748',
      fontWeight: '700',
      fontSize: '3rem',
      maxWidth: '450px',
    },

    '@media screen and (max-width: 576px)': {
      '& h1.title': {
        fontSize: '2rem',
      },
    },

    '& .paragraph': {
      color: '#718096',
      fontWeight: '500',
      maxWidth: '500px',
      fontSize: '1.3rem',
      '& span': {
        fontWeight: '700',
        color: '#4a5568',
      },
    },

    '& h3.title': {
      color: '#2d3748',
      fontWeight: '500',
      maxWidth: '250px',
      position: 'relative',
      '& .bold': {
        fontWeight: '700',
      },
    },

    '& .features': {
      '& .title': {
        color: '#2d3748',
        fontWeight: '600',
      },
      '& .description': {
        color:  '#718096',
        fontWeight: '400',
        maxWidth: '300px',
      },
    },

    '& .our-mission': {
      '& .paragraph': {
        fontSize: '1.1rem',
      },
    },

    '@media screen and (min-width: 992px)': {
      '& h3.title': {
        '&:after': {
          content: '""',
          position: 'absolute',
          bottom: 13,
          width: '50px',
          display: 'inline',
          borderTop: `3px solid ${'#cbd5e0'}`,
        },
      },

      '& .features': {
        '& h3.title': {
          '&:after': {
            right: -70,
          },
        },
      },

      '& .our-mission': {
        '& h3.title': {
          '&:after': {
            right: 0,
          },
        },
      },
    },
  },
});

export default Styles;
