import React , { useState, useEffect } from 'react' ;
import { WEBSITE_NAME } from '../../../utils/websiteData';
import Helmet from 'react-helmet';
import Offer from "./Offer";
import useStyles from './Offers-jss';
import SearchBar from '../Searchbar';
import CountryList from '../Cont';



const Offers = (props) => {  

    const classes = useStyles() ;

    const [input, setInput] = useState('');
    const [countryListDefault, setCountryListDefault] = useState();
    const [countryList, setCountryList] = useState();

  const fetchData = async () => {
    return await fetch('https://restcountries.eu/rest/v2/all')
      .then(response => response.json())
      .then(data => {
         setCountryList(data) 
         setCountryListDefault(data)
       });}

  const updateInput = async (input) => {
     const filtered = countryListDefault.filter(country => {
      return country.name.toLowerCase().includes(input.toLowerCase())
     })
     setInput(input);
     setCountryList(filtered);
  }

  useEffect( () => {fetchData()},[]);
	
 


return (
        <div>
            <Helmet>
                <title> {`${WEBSITE_NAME}`}</title>
            </Helmet>
            <div>

                     <SearchBar 
                     input={input} 
                     onChange={updateInput}
                        />
                <CountryList countryList={countryList}/>
                {/* here we gonna add a search bar ! */}
            </div>
            <div className={`${classes.page} card-shadow`}>
                <ul className='container'>
                    <Offer title='Medical' desc='medical services' />
                    <Offer title='wedding' desc='other description' />
                    <Offer title='death' desc='some description' />
                </ul>

            </div>
            
        </div>
);}

export default Offers ;
