import React  from "react";
import Helmet from "react-helmet";
import { WEBSITE_NAME } from "../../../utils/websiteData";
import './blog.css'
import useStyles from './Offers-jss';
import DonationIcon from '../../../images/donation-icon.svg';
import Solidarity from '../../../images/solidarity.svg';
import BloodIcon from '../../../images/blood-icon.svg';
import Other from '../../../images/other.svg';
import styled from 'styled-components';

const StyledDiv = styled.div`
  width: 60%;
  margin: 16px auto;
  border: 1px solid #eee;
  box-shadow: 0 2px 3px #ccc;
  padding: 16px;
  text-align: center;

  @media (min-width: 500px) {
    width: 450px;
  }
`;

const Offers = () => {

  const classes = useStyles();

  return(
      <div>
        <StyledDiv>
        <Helmet>
        <title>{`${WEBSITE_NAME} | Offers`}</title>
        </Helmet>
        {/*<input className='input-text' 
               placeholder='search ...'
               type='text'
               name='search'
              required />*/}
        <div className={`${classes.root} card-shadow text-center`}>
        
            <div className='row text-center text-lg-left'>
            <div className='col-12 col-lg-3'>
              <div>
                <img
                  className='img img-fluid mb-3'
                  src={DonationIcon}
                  alt='Home'
                />
                <h5 className='title mb-2 mt-2 mx-auto mx-lg-0'>Social aides</h5>
                <p className='description mb-5 mx-auto mx-lg-0'>
                   Give donations to those in need of money, tools or resources , specially ...
                </p>
              </div>
            </div>

            <div className=' boite col-12 col-lg-3'>
              <div>
              <img
                  className='img img-fluid mb-3'
                  src={BloodIcon}
                  alt='Home'
                />
                
                <h5 className='title mb-2 mt-2 mx-auto mx-lg-0'>
                  Medical Services 
                </h5>
                <p className='description mb-5 mx-auto mx-lg-0'>
                    Help patients in their chirurgical operations or medical treatment
                </p>
              </div>
            </div>

            <div className='col-12 col-lg-3'>
              <div>
                <img
                  className='img img-fluid mb-3'
                  src={Solidarity}
                  alt='Home'
                />
                
                <h5 className='title mb-2 mt-2 mx-auto mx-lg-0'>Solidarity services</h5>
                <p className='description mb-5 mx-auto mx-lg-0'>
                     Add a request to benefit of our list of donations 
                </p>
              </div>
            </div>
            
            <div className='col-12 col-lg-3'>
              <div>
                <img
                  className='img img-fluid mb-3'
                  src={Other}
                  alt='Home'
                />
                <h5 className='title mb-2 mt-2 mx-auto mx-lg-0'>Social aides</h5>
                <p className='description mb-5 mx-auto mx-lg-0'>
                   Give donations to those in need of money, tools or resources , specially ...
                </p>
              </div>
            </div>
          </div>

            
        </div>
        </StyledDiv>

      </div>
  )
}

export default Offers ;
