import React from 'react';
import { Link } from 'react-router-dom';
import styled from 'styled-components';
import useStyles from './Offers-jss';

import BloodIcon from '../../../images/blood-icon.svg'
const StyledDiv = styled.div`
  width: 60%;
  margin: 16px auto;
  border: 1px solid #eee;
  box-shadow: 0 2px 3px #ccc;
  padding: 16px;
  text-align: center;

  @media (min-width: 500px) {
    width: 450px;
  }
`;



const Offer = (props) => {
  const classes = useStyles();

  
    return(

        <StyledDiv>   
          <div className={`${classes.root} text-center`} >
                <img
                  className='img img-fluid mb-3'
                  src={BloodIcon}
                  alt='Home'
                />

                <h3>{props.title}</h3>
                <p className='subtitle'>{props.desc}</p>
              <Link  to='/offer-show'>
                  <button className='button-primary '> View Offers </button>
              </Link>
          </div>
        </StyledDiv>
    )
  
}

export default Offer ;
