import React  from "react";
//import useStyles from "./Request-jss";
import { Helmet } from "react-helmet";
import { WEBSITE_NAME } from "../../../utils/websiteData";
import Category from './category';
import useStyles from "./Request-jss";



 const Request = () => {

  const classes = useStyles() ;

  const onChange = (e) => { };

  const onChangeImages = (e) => { };

  const onSubmit = async (e) => { }
   
    return(
      <>
      
      <Helmet>
        <title>{`${WEBSITE_NAME} | Add request`}</title>
      </Helmet>
      <div className={`${classes.page} card-shadow text-center`} >
        <h2 className='title'>Add a Request</h2>
        <h5 className='subtitle'>
          Choose your offer </h5>
        <div className='row'>
          <div className='col'>
            {' '}
            <form className='add-form mx-auto mt-4' onSubmit={onSubmit}>
              
              <Category/>
              <br/>

              <div className='form-group'>
                <textarea
                  className='input-text'
                  id='content'
                  name='content'
                  placeholder='Content'
                  onChange={onChange}
                  rows='3'
                ></textarea>
              </div>

              <div className='form-group'>
                <input
                  id='images'
                  className='input-text'
                  type='file'
                  name='images'
                  placeholder='Choose file'
                  onChange={onChangeImages}
                  multiple /> 
                <label htmlFor='images'>
                </label>
              </div>

                <input
                  type='submit'
                  value='Add request'
                  className='button-primary mt-3'
                />

            </form>
          </div>
        </div>
      </div>
    </>

    )
}
export default Request ;
