import React , { useState } from "react";
import useStyles from "./Request-jss";
import { Helmet } from "react-helmet";
import { WEBSITE_NAME } from "../../../utils/websiteData";
import { Component } from "react";


let title = null ;
let category
let content
const classes = useStyles();


class Request extends Component {
  state = {
    category: 0,
    title: '',
    content: '',
}



onCategory = (e) => {
  this.setState({ ...this.state, category : e.target.value });
  console.log(this.state)
}

onChangeImages = (e) => setPost({ ...post, images: e.target.files });

onSubmit = async (e) => {
  e.preventDefault();

  if (title === '') {
    console.log('Title is empty');
    return;
  }

  if (category === 0 || category === '0') {
    console.log('Please select a category');
    return;
  }

  

  if (content === '') {
    console.log('Content is empty');
    return;
  }
}

  render(){
    return(
      <>
      <Helmet>
        <title>{`${WEBSITE_NAME} | Add request`}</title>
      </Helmet>
      <div className={`${classes.page} card-shadow text-center`}>
        <h3 className='title'>Add a Request</h3>
        <h6 className='subtitle'>
          Ask for donation from your organization
        </h6>

        <div className='row'>
          <div className='col'>
            {' '}
            <form className='add-form mx-auto mt-4' onSubmit={onSubmit}>
              

              <div className='form-group'>
                <select
                  id='category'
                  name='category'
                  value={category}
                  onChange={onCategory}
                  className='custom-select input-select input-text' >
                    <option value='0'>All categories</option>
                    <>
                      <option key='1' value='1'>Social aides</option>
                      <option key='2' value='2'>Medical services</option>
                      <option key='3' value='3'>Solidarity services</option>
                      <option key='4' value='4'>Other services</option>
                    </>
                </select>
              </div>


              <div className='form-group'>
                <select
                id='title'
                name='title'
                onChange={onChange}
                value={title}
                className='custom-select input-select input-text' >
                    <option value='0'>All offers</option>
                    <>
                      <option key='1' value='1'>Social aides</option>
                      <option key='2' value='2'>Medical services</option>
                      <option key='3' value='3'>Solidarity services</option>
                      <option key='4' value='4'>Other services</option>
                    </>
                </select>
              </div>

              
                 

              <div className='form-group'>
                <textarea
                  className='input-text'
                  id='content'
                  name='content'
                  placeholder='Content'
                  onChange={onChange}
                  rows='3'
                ></textarea>
              </div>

              <div className='form-group'>
                <input
                  id='images'
                  type='file'
                  name='images'
                  placeholder='Choose file'
                  onChange={onChangeImages}
                  multiple /> 
                <label htmlFor='images'>
                </label>
              </div>
                <input
                  type='submit'
                  value='Add request'
                  className='button-primary mt-3'
                />
            </form>
          </div>
        </div>
      </div>
    </>

    )
  }
}
export default Request ;
