import React, { useState }  from "react";
//import useStyles from "./Request-jss";
import { Helmet } from "react-helmet";
import { WEBSITE_NAME } from "../../../utils/websiteData";
import InputElement from './InputElement';
import useStyles from "./Request-jss";



 const Request = () => {

  const [ state , setState ] = useState({ cat : null , kind : null })

  const classes = useStyles() ;

  const categorychange = (event) => {
      setState({ cat : EventTarget.value })
      console.log(state)
  }
  
  const onSubmit = async (e) => { }
   
    return(
      <>
      
      <Helmet>
        <title>{`${WEBSITE_NAME} | Requests`}</title>
      </Helmet>
      <div className={`${classes.page} card-shadow text-center`} >
        <h2 className='title'>Add a Request</h2>
        <h5 className='subtitle'>
          Choose your offer  </h5>
        <div className='row'>
          <div className='col'>
            {' '}
            <form className='add-form mx-auto mt-4' onSubmit={onSubmit}>


                 <InputElement name='category' pardefaut='All categories' 
                               el1='Social aids' el2='Medical services' el3='Solidarity aid' el4='Exceptional loans'
                               click={categorychange} />

                 <InputElement name='category' pardefaut='All categories' 
                               el1='Social aids' el2='Medical services' el3='Solidarity aid' el4='Exceptional loans'
                               click={categorychange} />  


                <input
                  type='submit'
                  value='Add request'
                  className='button-primary mt-3'
                />
            </form>
          </div>
        </div>
      </div>
    </>

    )
}
export default Request ;

