import React, { useState, useEffect } from 'react';
import { connect } from 'react-redux';
import Helmet from 'react-helmet';

// Actions

import {
  updateProfile,
  updatePassword,
  loadProfile,
  clearErrors,
} from '../Auth/Auth';
import { setAlert } from '../Auth/Auth';

// Utils
import { WEBSITE_NAME } from '../../utils/websiteData';

// Components
import Sidebar from '';
import Spinner from '../Spinner/Spinner';

import useStyles from './profile-jss';

const Profile = (props) => {
  const {
    profile,
    error,
    loading_profile,
    clearErrors,
      updateProfile,
  } = props;

  const classes = useStyles();

  useEffect(() => {
    if (error) {
      setAlert(error);
      clearErrors();
    }

    //eslint-disable-next-line
  }, [error]);

  const [user, setUser] = useState({
    username: '',
    email: '',
    full_name: '',
    phone: '',
  });

  const {
    username,
    email,
    full_name,
    phone,
  } = user;

  useEffect(() => {
    if (profile) {
      setUser({
        ...user,
        ...profile,
        full_name: profile.full_name ? profile.full_name : '',
        phone: profile.phone ? profile.phone : '',
        
      });
      
    }

    //eslint-disable-next-line
  }, [profile]);

  const onChangeBasic = (e) =>
    setUser({ ...user, [e.target.name]: e.target.value });

  const onSubmit = async (e) => {
    e.preventDefault();

    if (phone === '') {
      setAlert('Phone is empty');
      return;
    }

    await updateProfile({
      full_name,
      phone,
      
    });
  };
  return (
    <>
      <Helmet>
        <title>{`${WEBSITE_NAME} | Profile`}</title>
      </Helmet>
      <div className={`${classes.page} card-shadow text-center`}>
        <h3 className='title'>Profile</h3>
        <h6 className='subtitle'>Change your phone number & other info</h6>

        <div className='content mt-5'>
          <div className='row'>
            <div className='col-12 col-lg-9'>
              {profile !== null && !loading_profile ? (
                <>
                  <h6 className='text-left'>Edit your profile</h6>

                  <div className='section basic-info mt-4'>
                    <h5 className='title text-left mt-2'>
                      Basic informations & Preferences
                    </h5>

                    <form onSubmit={onSubmit}>
                      <div className='row'>
                        <div className='form-group col-12 col-lg-6'>
                          <label htmlFor='username' className='float-left'>
                            Username
                          </label>
                          <input
                            className='input-text'
                            type='text'
                            id='username'
                            name='username'
                            value={username}
                            placeholder='Username'
                            readOnly
                          />
                        </div>

                        <div className='form-group col-12 col-lg-6'>
                          <label htmlFor='email' className='float-left'>
                            Email address
                          </label>
                          <input
                            className='input-text'
                            type='text'
                            id='email'
                            name='email'
                            value={email}
                            placeholder='Email address'
                            readOnly
                          />
                        </div>

                        <div className='form-group col-12 col-lg-6'>
                          <label htmlFor='full_name' className='float-left'>
                            Full name
                          </label>
                          <input
                            className='input-text'
                            type='text'
                            id='full_name'
                            name='full_name'
                            value={full_name}
                            placeholder='Full name'
                            onChange={onChangeBasic}
                          />
                        </div>

                        <div className='form-group col-12 col-lg-6'>
                          <label htmlFor='phone' className='float-left'>
                            Phone
                          </label>
                          <input
                            className='input-text'
                            type='text'
                            id='phone'
                            name='phone'
                            value={phone}
                            placeholder='Phone'
                            onChange={onChangeBasic}
                          />
                        </div>
                      </div>

                      <div className='row'>
                        <div className='col'>
                          <input
                            type='submit'
                            value='Save'
                            className='button-gray float-right mt-2'
                          />
                        </div>
                      </div>
                    </form>
                  </div>

                  
                </>
              ) : (
                <div className='content text-center mt-5'>
                  <Spinner />
                </div>
              )}
            </div>
            <div className='d-none d-lg-block col-12 col-lg-3'>
              <Sidebar />
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

const mapSateToProps = (state) => ({
  profile: state.auth.user,
  loading_profile: state.auth.loading_profile,
  error: state.auth.error,
});
export default connect(mapSateToProps, {
  clearErrors,
  loadProfile,
  updateProfile,
  updatePassword,
})(Profile);
