import React , { useState , useEffect } from 'react' ;
import Helmet from 'react-helmet';
import useStyles from '../Requests/Request-jss';


const Request = () => {

    const classes = useStyles() ;

    const [post_search, setSearch] = useState({
        keywords: '',
        category: 0,
       
      });
    
      const { keywords, category} = post_search;
    
      useEffect(() => {
        getRequests(keywords, category);
        getCategories();
    
    
        //eslint-disable-next-line
      }, []);
    
     
    
        
    
      const onChange = (e) =>
        setSearch({ ...post_search, [e.target.name]: e.target.value });
    
      const onSubmit = (e) => {
        e.preventDefault();
        getRequests(keywords, category);
      };


    return 
    (
        <>
          <Helmet>
            <title>{`${WEBSITE_NAME} | Add request`}</title>
          </Helmet>
          <div className={`${classes.page} card-shadow text-center`}>
            <h3 className='title'>Add a Request</h3>
            <h6 className='subtitle'>
              Ask for donation from your organization
            </h6>
    
            <div className='row'>
              <div className='col'>
                {' '}
                <form className='add-form mx-auto mt-4' onSubmit={onSubmit}>
                  <div className='form-group'>
                    <input
                      className='input-text'
                      type='text'
                      id='title'
                      name='title'
                      value={title}
                      placeholder='Title'
                      onChange={onChange}
                    />
                  </div>
    
                  <div className='form-group'>
                    <select
                      id='category'
                      name='category'
                      value={category}
                      onChange={onChange}
                      className='custom-select input-select input-text'
                    >
                      {loading_categories ? (
                        <option value='0'>Type</option>
                      ) : (
                        <>
                          <option value='0'>All categories</option>
                          {categories !== null &&
                            categories.length > 0 &&
                            categories.map((category) => (
                              <option key={category.id} value={category.id}>
                                {category.label}
                              </option>
                            ))}
                        </>
                      )}
                    </select>
                  </div>
    
                  
                     
    
                  <div className='form-group'>
                    <textarea
                      className='input-text'
                      id='content'
                      name='content'
                      value={content}
                      placeholder='Content'
                      onChange={onChange}
                      rows='3'
                    ></textarea>
                  </div>
    
                  <div className='form-group'>
                    <input
                      id='images'
                      type='file'
                      name='images'
                      onChange={onChangeImages}
                      multiple
                    />
                    <label htmlFor='images'>
                      Choose Images ({images ? images.length : 0})
                    </label>
                  </div>
                  {loading_add_post ? (
                    <Spinner />
                  ) : (
                    <input
                      type='submit'
                      value='Add request'
                      className='button-primary mt-3'
                    />
                  )}
                </form>
              </div>
            </div>
          </div>
        </>
      );
}

export default Request ;
