import React , { Component }   from 'react' ;
import { Link } from 'react-router-dom';
import { Helmet } from 'react-helmet';
// import Auxe from '../../Layout/Auxe/Auxe';
import { WEBSITE_NAME } from '../../../utils/websiteData';
import useStyles from './login-jss';


const login = () => {

  const {
    loading,
    login,
    setAlert,
  } = props;

  const classes = useStyles();
  const [user, setUser] = useState({
    username: '',
    password: '',
  });

  const { username, password } = user;

  const onChange = (e) => setUser({ ...user, [e.target.name]: e.target.value });

  const onSubmit = (e) => {
    e.preventDefault();

    if (username === '' || password === '') {
     setAlert('Please enter all fields');
    } else {
      login({ username, password });
    }
  };


  return (
    <>
    <Helmet>
      <title>{`${WEBSITE_NAME} | Login`}</title>
    </Helmet>
    <div className={`${classes.root} card-shadow text-center`}>
      <h3 className='title'>Sign in</h3>
      <h6 className='subtitle'>Use your email and password provided<br></br> by your organization</h6>

      <form className='mt-4' onSubmit={onSubmit}>
        <div className='form-group'>
          <input
            className='input-text'
            type='text'
            name='username'
            value={username}
            placeholder='Username'
            onChange={onChange}
            required
          />
        </div>

        <div className='form-group'>
          <input
            className='input-text'
            type='password'
            name='password'
            value={password}
            placeholder='Password'
            onChange={onChange}
            required
          />
        </div>

        
          <input
            type='submit'
            value='Login'
            className='button-primary mt-3'
          />
        
      </form>

      <p className='form-link mt-3'>
        Login Trouble?{' '}
        <Link to='/login'>
          <span>Reset password</span>
        </Link>
      </p>
    </div>
  </>
  );
}

export default login ;
