import React from 'react' ;
import { useState } from 'react';
import useStyles from './Request-jss';


const category = () => {

  const classes = useStyles()

  const [category , setCategory] = useState({ category : null})

  const changeCategory = (event) =>{ setCategory({category : event.target.value})}
    return (
    <div className={`${classes.root} text-center`}>   
        <div className='form-group'>
                <select
                  id='category'
                  name='category'
                  onChange={changeCategory}
                  className='custom-select input-select input-text' >
                    <option value='0'>All categories</option>
                    <>
                      <option key='1' value='1'>Social aides</option>
                      <option key='2' value='2'>Medical services</option>
                      <option key='3' value='3'>Solidarity services</option>
                      <option key='4' value='4'>Other services</option>
                    </>
                </select>
        </div>
    </div> 
    )
}

export default category ;
