import React, { useState, useEffect } from 'react';
import { connect } from 'react-redux';
import Helmet from 'react-helmet';
import { Container, Form, Button } from 'react-bootstrap';

// Actions
import { loadUserProfile } from '../../../redux/actions/userActions';
import { setAlert } from '../../../redux/actions/alertActions';

// App layout components
import Spinner from '../../layout/Spinner';
import Dash from '../Admin/Dash'
// Utils
import { WEBSITE_NAME } from '../../../utils/Data';


const Profile = (props) => {
  const {
    match,
    user,
    user_profile,
    loading,
    loading_send,
    error_send,
    loadUserProfile,
    sendMessage,
    clearErrors,
    setAlert,
  } = props;
  const { _id, name, username, bio, allow_messages } =
    user_profile || {};

  const [message, setMessage] = useState('');
  const [is_sent, setIsSent] = useState(false);

  const onChange = (e) => setMessage(e.target.value);

  useEffect(() => {
    loadUserProfile(match.params.username);

    // eslint-disable-next-line
  }, []);

  useEffect(() => {
    loadUserProfile(match.params.username);

    // eslint-disable-next-line
  }, [match]);

  useEffect(() => {
    if (error_send && error_send.length) {
      if (typeof error_send === 'object') {
        error_send.forEach((err) => {
          setAlert(err.msg, 'danger');
        });
      } else {
        setAlert(error_send, 'danger');
      }

      clearErrors();
    }

    // eslint-disable-next-line
  }, [error_send]);

  const onSubmit = async (e) => {
    e.preventDefault();

    if (message === '') {
      setAlert('Message cannot be empty', 'danger');
      return;
    }

    await sendMessage({ user_id: _id, content: message });

    if (!error_send) {
      setIsSent(true);
    }
  };


  return (
    <>
      <Helmet>
        <title>{`${WEBSITE_NAME} | ${
          loading ? 'Loading...' : username || 'Not found'
        }`}</title>
      </Helmet>
      <Container>
        <div className='container-inner px-3 mt-4 text-center'>
          {loading ? (
            <Spinner />
          ) :  !user || user._id === '612caa1f31cb9a33a0193de2' ? (
            <Dash />
          ) : (
            <div className='profile mx-auto'>


              <div className='user-details mt-4'>
                <h3 className='name'>{name || username}</h3>
                <p className='title'>
                  Position : { bio }
                </p>

              </div>

              {!user || user._id !== _id ? (
                allow_messages ? (
                  <Form
                    className='form form-container mt-4'
                    onSubmit={onSubmit}
                  >
                    <Form.Group>
                      <Form.Label>Your message</Form.Label>
                      <Form.Control
                        as='textarea'
                        rows='3'
                        name='message'
                        value={message}
                        onChange={onChange}
                        disabled={is_sent}
                      />
                    </Form.Group>

                    <div className='links d-flex align-items-center justify-content-center mt-4'>
                      {loading_send ? (
                        <Spinner />
                      ) : is_sent ? (
                        <div className='message-sent'>Message sent!</div>
                      ) : (
                        <Button variant='primary' type='submit'>
                          Send
                        </Button>
                      )}
                    </div>
                  </Form>
                ) : (
                  <div className='mt-4'>
                    <h6>Receiving messages is disabled</h6>
                  </div>
                )
              ) : (
                <div className='share mt-5'>

                </div>
              )}
            </div>
          )}
        </div>
      </Container>
    </>
  );
};

const mapSateToProps = (state) => ({
  user: state.auth.user,
  user_profile: state.user.user_profile,
  loading: state.user.loading,
});

export default connect(mapSateToProps, {
  loadUserProfile,
  setAlert,
})(Profile);
