import React, { useState, useEffect } from 'react';
import { connect } from 'react-redux';
import Helmet from 'react-helmet';

// Actions
/*import {
  getStates,
  getStateCities,
  clearCities,
} from '../../../../redux/actions/locationActions';*/
import {
  addPost,
  getCategories,
  clearErrors,
} from '../../../../redux/actions/postActions';
import { setAlert } from '../../../../redux/actions/alertActions';

// Utils
import { WEBSITE_NAME } from '../../../../utils/websiteData';

// Components
import Spinner from '../../../layout/Spinner';

import useStyles from '../posts-jss';

const AddRequest = (props) => {
  const classes = useStyles();

  const {
    categories,
    loading_add_post,
    loading_categories,
    error,

    addPost,
    getCategories,

    clearErrors,
    setAlert,
  } = props;

  const [post, setPost] = useState({
    title: '',
    category: 0,
    content: '',
    images: null,
  });

  const { title, category,  content, images } = post;

  useEffect(() => {
    getCategories();
    getStates();

    //eslint-disable-next-line
  }, []);

  useEffect(() => {
    if (error) {
      setAlert(error);
      clearErrors();
    }

    // eslint-disable-next-line
  }, [error]);


  const onChange = (e) => setPost({ ...post, [e.target.name]: e.target.value });

  const onChangeImages = (e) => setPost({ ...post, images: e.target.files });

  const onSubmit = async (e) => {
    e.preventDefault();

    if (title === '') {
      setAlert('Title is empty');
      return;
    }

    if (category === 0 || category === '0') {
      setAlert('Please select a category');
      return;
    }



    if (content === '') {
      setAlert('Content is empty');
      return;
    }

    const formData = new FormData();

    formData.append('title', title);
    formData.append('post_category', category);
    formData.append('post_type', 2);
    formData.append('content', content);

    if (images)
      for (let i = 0; i < images.length; i++) {
        formData.append('images', images[i]);
      }

    await addPost(formData);

    window.location.href = '/posts';
  };

  return (
    <>
      <Helmet>
        <title>{`${WEBSITE_NAME} | Add request`}</title>
      </Helmet>
      <div className={`${classes.page} card-shadow text-center`}>
        <h3 className='title'>Add a Request</h3>
        <h6 className='subtitle'>
          Ask for donation from your organization
        </h6>

        <div className='row'>
          <div className='col'>
            {' '}
            <form className='add-form mx-auto mt-4' onSubmit={onSubmit}>
              <div className='form-group'>
                <input
                  className='input-text'
                  type='text'
                  id='title'
                  name='title'
                  value={title}
                  placeholder='Title'
                  onChange={onChange}
                />
              </div>

              <div className='form-group'>
                <select
                  id='category'
                  name='category'
                  value={category}
                  onChange={onChange}
                  className='custom-select input-select input-text'
                >
                  {loading_categories ? (
                    <option value='0'>Type</option>
                  ) : (
                    <>
                      <option value='0'>All categories</option>
                      {categories !== null &&
                        categories.length > 0 &&
                        categories.map((category) => (
                          <option key={category.id} value={category.id}>
                            {category.label}
                          </option>
                        ))}
                    </>
                  )}
                </select>
              </div>




              <div className='form-group'>
                <textarea
                  className='input-text'
                  id='content'
                  name='content'
                  value={content}
                  placeholder='Content'
                  onChange={onChange}
                  rows='3'
                ></textarea>
              </div>

              <div className='form-group'>
                <input
                  id='images'
                  type='file'
                  name='images'
                  onChange={onChangeImages}
                  multiple
                />
                <label htmlFor='images'>
                  Choose Images ({images ? images.length : 0})
                </label>
              </div>
              {loading_add_post ? (
                <Spinner />
              ) : (
                <input
                  type='submit'
                  value='Add request'
                  className='button-primary mt-3'
                />
              )}
            </form>
          </div>
        </div>
      </div>
    </>
  );
};

const mapSateToProps = (state) => ({
  categories: state.posts.categories,
  loading_add_post: state.posts.loading_add_post,
  loading_categories: state.posts.loading_categories,
  error: state.posts.error,

});

export default connect(mapSateToProps, {
  addPost,
  getCategories,
  clearErrors,

  setAlert,
})(AddRequest);
